class Solution {
public:
    bool isV(char c){
        return (c == 'a' || c== 'e' || c =='i' || c == 'o' || c == 'u');
    }
    int maxVowels(string s, int k) {
        int ans = 0;
        int curr = 0;
        int i = 0, j = 0;
        while(j < k){
            curr += isV(s[j]);
            j++;
        }
        ans = curr;
        while(j < s.length()){
            curr += isV(s[j]);
            curr -= isV(s[i]);
            i++;
            j++;
            ans = max(ans, curr);
        }
        return ans;
    }
};
