class Solution {
public:
    long long numberOfWeeks(vector<int>& vec) {

        sort(vec.begin(),vec.end(),greater<int>());
        long long int sum=0;
        long long  pass=(vec[0]-1);
        sum+=vec[0];
        for(int i=1;i<vec.size();i++){
            sum+=vec[i];
            pass=pass-min(vec[0]-1,vec[i]);
        }
        if(pass<=0) return sum;
        return sum-pass;

    }
};
