class Solution {
public:
    int mostWordsFound(vector<string>& sentences) {
        int _max = INT_MIN;
        for(auto i : sentences){
            int cnt = 0;
            for(auto j : i){
                if(j == ' '){
                    cnt++;
                }
            }
            cnt++;
            _max = max(_max, cnt);
        }
        return _max;

    }
};
