class Solution {
public:

    int fun(vector<int> a) {
        int maxi = INT_MIN;
        int mini = INT_MAX;

        for(auto i : a) {
            maxi = max(maxi, i);
            mini = min(mini, i);
        }
        return maxi-mini;
    }
    int maxAbsValExpr(vector<int>& a, vector<int>& b) {
        int  n  = a.size();
      vector<int> v1(n);
        vector<int> v2(n);
        vector<int> v3(n);
        vector<int> v4(n);

        for(int i=0;i<n;i++) {
            v1[i] = a[i]+b[i]+i;
            v2[i] = a[i]+b[i]-i;
            v3[i] = a[i]-b[i]+i;
            v4[i] = a[i]-b[i]-i;
        }

        return max({fun(v1), fun(v2), fun(v3), fun(v4)});
    }
};
