class Solution {
public:
    long long maximumOr(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> pre(n, 0);
        vector<int> suff(n, 0);
        int o = 0;
        for(int i = 0; i < n; i++){
            pre[i] = o;
            o |= nums[i];
        }
        o = 0;
        for(int i = n- 1; i >=0;i--){
            suff[i] = o;
            o |= nums[i];
        }
        long long ans = 0;
        for(int i = 0; i < n; i++){
            long long int t1 = pre[i] | suff[i] | (nums[i] * 1LL << k);
            ans = max(ans, t1);
        }
        return ans;
    }
};
