class Solution {
public:
    int maxScore(vector<int>& arr, int k) {
        int sum = 0;
        int index = arr.size()-1;
        while(k--){
            sum+=arr[index];
            index--;
        }
        // cout<<sum <<" "<<index;

        int pos = 0;

        int newSum = sum;

        for(int i = index+1 ; i<arr.size();i++){
            newSum = newSum-arr[i]+arr[pos];
            pos++;
            sum = max(sum,newSum);
        }
        return sum;
    }
};
