class Solution {
public:
    const int mod = 1000000007;
    int maximumProduct(vector<int>& nums, int k) {
        priority_queue<int, vector<int>, greater<int> > q;
        for(auto i : nums) {
            q.push(i);
        }

        while(k--) {
            int t = q.top();
            t += 1;
            q.pop();
            q.push(t);
        }

        long long ans =  1;
        while(!q.empty()) {
            ans *= q.top();
            q.pop();
            ans = (ans + mod) % mod;
        }
        return ans;
    }
};
