class Solution {
public:
    int maxProduct(vector<string>& words) {
        vector<vector<int>> arr(words.size(), vector<int>(26,0));
        for(int i = 0 ; i < words.size();i++){
            for(auto w : words[i]){
                arr[i][w-'a']++;
            }
        }
        int ans = 0;
        for(int i = 0 ; i < words.size();i++){
            for(int j = i + 1; j < words.size() ; j ++){
                int f = 0;
                for(int k = 0; k < 26;k++){
                    if(arr[i][k] > 0 && arr[j][k] > 0){
                        f = 1;
                        break;
                    }
                }
                if(f)continue;
                ans = max((int)words[i].length() * (int) words[j].length(), ans);
            }
        }
        return ans;
    }
};
