class Solution {
public:

    bool ispossible(vector<int> &batteries, int n, long long mid) {
        long long sum = 0;
        for(auto i : batteries)
        {
            sum += min(mid, i*1LL);
        }
        return sum >= mid * n;
    }
    long long maxRunTime(int n, vector<int>& batteries) {
        sort(batteries.begin(), batteries.end());
        long long s = 1;
        long long e = 1e14;
        long long ans = 0;

        while(s <= e) {
            long long mid = (s + e) >> 1;
            if(ispossible(batteries, n, mid)) {
                ans = max(ans, mid);
                s = mid + 1;
            }
            else {
                e = mid - 1;
            }
        }
        return ans;
    }
};
