class Solution {
public:
    int maximumScore(int a, int b, int c) {
        priority_queue<int> q;
        q.push(a);        
        q.push(b);
        q.push(c);
        int ans = 0;

        while(q.size() > 1){
            int max1 = q.top();
            q.pop();
            int max2 = q.top();
            q.pop();
            ans++;
            if(max1 - 1 != 0) q.push(max1-1);
            if(max2 - 1!=0) q.push(max2-1);

        }
        return ans;






    }
};
