class Solution {
public:
    int kadane(vector<int> &arr){
        int curr = 0, _max = 0;
        for(auto i : arr){
            curr += i;
            if(curr < 0)curr = 0;
            _max = max(_max, curr);
        }
        return _max;
    }
    int maximumsSplicedArray(vector<int>& nums1, vector<int>& nums2) {
        int n = nums1.size();
        vector<int> arr1(n, 0), arr2(n, 0);
        for(int i = 0; i < n; i++){
            arr1[i] = nums2[i] - nums1[i];
            arr2[i] = -arr1[i];
        }
        int s1 = accumulate(nums1.begin(), nums1.end(), 0);
        int s2 = accumulate(nums2.begin(), nums2.end(), 0);
        int ans = max(s1, s2);
        ans = max({ans, s1 + kadane(arr1), s2 + kadane(arr2)});
        return ans;
    }
};
