class Solution {
public:
    int helper(vector<string>& words, map<char, int> &mp, vector<int>&score, int ind){
        for(auto i : mp)if(i.second < 0)return -1e6;
        if(ind == words.size())return 0;
        int nt = 0 + helper(words, mp, score, ind + 1);
        int s = 0;
        for(auto i : words[ind]){
            mp[i]--;
            s += score[i - 'a'];
        }
        int tk = s + helper(words, mp, score, ind + 1);
        for(auto i : words[ind]){
            mp[i]++;
            s -= score[i - 'a'];
        }
        return max(tk, nt);
    }
    int maxScoreWords(vector<string>& words, vector<char>& letters, vector<int>& score) {
        map<char, int> mp;
        for(auto i : letters)mp[i]++;
        int ans = helper(words, mp, score, 0);
        // if(ans < 0)return 0;
        return ans;
    }
};
