class Solution {
public:
    vector<long long> maximumEvenSplit(long long finalSum) {
        vector<long long> ans;
        if(finalSum % 2 != 0) {
            return ans;
        }

        long long t = 1;

        while(finalSum - 2 * t >= 0) {
            // cout << 2 * t << endl;
            ans.push_back(2 * t);
            finalSum -= 2 * t;
            t++;


        }

        ans[ans.size() - 1] = ans[ans.size() - 1] + finalSum;
        return ans;

    }
};
