class Solution {
public:
    int maxStarSum(vector<int>& vals, vector<vector<int>>& edges, int k) {
        unordered_map<int,vector<int>> umap;
        int ans=INT_MIN;
       for(auto i:vals) ans=max(ans,i);
        for(auto i:edges){
            umap[i[0]].push_back(vals[i[1]]);
            umap[i[1]].push_back(vals[i[0]]);
        }
        for(auto &i:umap){
            sort(begin(i.second),end(i.second),greater<int>());
        }

        for(auto i:umap){
            int sum=vals[i.first];

                int cnt=0;
                for(auto ngh:i.second){

                    if(cnt>=k) break;
                    if(ngh>0){
                        cnt++;
                        sum+=ngh;
                    }
                }


            ans=max(sum,ans);
        }
        return ans;
    }
};
