class Solution:
    ans = -1e18
    def helper(self, nums, ind, prod, took):
        if ind == len(nums):
            global ans
            if took:
                self.ans = max(self.ans, prod)
            return
        self.helper(nums, ind + 1, prod * nums[ind], True)
        self.helper(nums, ind + 1, prod, took)
    def maxStrength(self, nums: List[int]) -> int:
        self.helper(nums, 0, 1, False)
        return int(self.ans)
