class Solution {
public:
    int maximumSwap(int num) {
        int maxdig = -1,right = -1,left = -1,maxidx = -1;
        string numstr = std::to_string(num);
        for(int i =numstr.size()-1;i>=0;i--){
            if(numstr[i]>maxdig){
                maxdig = numstr[i];
                maxidx = i;
                continue;
            }
            if(numstr[i]<maxdig){
                left = i;
                right = maxidx;
            }


        }
        if(left==-1){
            return num;
        }
        std::swap(numstr[left],numstr[right]);
        return std::stoi(numstr);

    }
};
