class Solution {
public:
    int maximumValue(vector<string>& strs) {
        int ans=0;
        for(auto i:strs){
        bool y=0;
            for(auto ii:i){
                if(ii>='a'&&ii<='z'){
                    ans=max(ans,(int)i.length());
                    y=1;
                    break;
                }
            }
            if(y==0){
                ans=max(ans,stoi(i));
            }
            y=0;
        }
        return ans;
    }
};
