/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:
    ListNode* mergeInBetween(ListNode* list1, int a, int b, ListNode* list2) {
        if(list2 == NULL){
            return list1;
        }
        int i=0;
        int j=0;
        ListNode* curr = list1;
        ListNode* post = list2;
        ListNode* c = list1;
        while(post->next){
            post = post->next;
        }
        while(i < b){
            c = c->next;
            i++;
        }
        c = c->next;


        while(j < a-1 ){
            curr=curr->next;
            j++;
        }
        curr->next = list2;

        post->next = c;
        return list1;
    }
};
