class Solution {
public:
    string mergeAlternately(string word1, string word2) {
        int i = 0, j = 0, c = 0;
        string ans;
        while(i < word1.size() && j < word2.size()){
            if(c == 0){
                ans.push_back(word1[i++]);
            }else{
                ans.push_back(word2[j++]);
            }
            c = 1 - c;
        }
        while(i < word1.size()){
            ans.push_back(word1[i++]);
        }
        while(j < word2.size()){
            ans.push_back(word2[j++]);
        }
        return ans;
    }
};
