bool cmpr(pair<int, int>&a, pair<int, int>&b){
    int diff1 = a.first - a.second;
    int diff2 = b.first - b.second;
    if(diff1 == diff2){
        return a.first > b.first;
    }else{
        return diff1 >= diff2;
    }
}
class Solution {
public:
    int solve(vector<pair<int, int>> arr, int k)
    {
        int ans = 0;
        for(int i = 0; i < k;i++){
            ans += arr[i].first;
        }
        for(int i = k ; i < arr.size();i++){
            ans += arr[i].second;
        }
        return ans;
    }
    int miceAndCheese(vector<int>& reward1, vector<int>& reward2, int k) {
        vector<pair<int, int>> arr;
        for(int i = 0; i < reward1.size();i++){
            arr.push_back({reward1[i], reward2[i]});
        }
        sort(arr.begin(), arr.end(), cmpr);
        int ans2 = solve(arr, k);
        return ans2;
    }
};
