class Solution {
public:
    int minMaxGame(vector<int>& nums) {
        if(nums.size()==1){ return nums[0];}
        vector<int> temp;
        bool isodd=1;
        for(int i=1;i<nums.size();i+=2){
            if(isodd){
                temp.push_back(min(nums[i],nums[i-1]));
            }else{
                cout<<i<<" ";
                temp.push_back(max(nums[i],nums[i-1]));
            }

            isodd=!isodd;
        }
        return minMaxGame(temp);
    }
};
