class Solution {
public:
    int minimumDeviation(vector<int>& nums) {
        priority_queue<int> maxpq;
        int _max = -1e9, _min = +1e9;
        for(auto i : nums){
            if(i%2 == 1){
                i *=2;
            }
            _min = min(_min, i);
            _max = max(_max, i);
            maxpq.push(i);
        }
        int ans = 1e9;
        while(maxpq.top()%2 == 0){
            int top = maxpq.top();
            maxpq.pop();
            ans = min(ans, top - _min);
            // cout<<ans<<endl;
            _min = min(_min, top/2);
            maxpq.push(top/2);
        }
        return min(ans, maxpq.top() - _min);
    }
};
