class Solution {
public:
    int minimizeArrayValue(vector<int> &nums)
    {
        int _max = -1e9;
        double sum = 0;
        for (int i = 0; i < nums.size(); i++)
        {
            sum += nums[i];
            _max = max(_max, (int)ceil(double(sum) / (i + 1)));
        }
        return _max;
    }
};
