class Solution {
public:


    int minimizeMax(vector<int> &nums, int p)
    {
        sort(nums.begin(), nums.end());
        int n = nums.size();
        int ans = 0, lo = 0, hi = 1e9;
        while(lo <= hi){
            int mid = (lo + hi)/2;
            int c = 0;
            for(int i = 0; i < n - 1;i++){
                if(nums[i + 1] - nums[i] <= mid){
                    i++;
                    c++;
                }
            }
            if(c >=p){
                ans = mid;
                hi = mid - 1;
            }else{
                lo = mid + 1;
            }
        }
        return ans;
    }
};
