class Solution {
public:
    int minimizeXor(int num1, int num2) {
        int k = 0;
        for(int i = 0; i < 32; i++){
            if((num2 & (1<<i)))k++;
        }
        int res = 0;
        for(int i = 31; i >=0 && k > 0; i--){
            if((num1 & (1<<i))){
                res |= (1<<i);
                k--;
            }
        }
        for(int i = 0; i < 32 && k > 0; i++){
            if((num1 & (1<<i)) == 0){
                res |= (1<<i);
                k--;
            }
        }
        return res;

    }
};
