class Solution {
public:
    int minAbsoluteDifference(vector<int>& nums, int x) {
        int n = nums.size();
        set<int> st;
        int ans = 1e9;
        for(int i = x; i < n; i++){
            st.insert(nums[i - x]);
            auto ind = st.lower_bound(nums[i]);
            if(ind != st.end()){
                ans = min(ans, abs(nums[i] - *ind));
                ind++;
                if(ind != st.end()){
                    ans = min(ans, abs(nums[i] - *ind));
                }
                ind--;
            }
            if(ind != st.begin()){
                ind--;
                ans = min(ans, abs(nums[i] - *ind));
                ind++;
            }
        }
        return ans;
    }
};
