class Solution {
public:
    int minAddToMakeValid(string s) {
        int open = 0;
        int count = 0;
        for(int i = 0;i<s.size();i++){
            char ch = s[i];
            if(ch == '('){
                open++;
            }
            else{
                if(open>0)open--;
                else count++;
            }
        }
        return count+open;
    }
};
