class Solution {
public:
    long long makeIntegerBeautiful(long long n, int target) {
        vector<int> arr;
        long long t = n;
        while(t > 0){
            arr.push_back(t%10);
            t/=10;
        }
        reverse(arr.begin(), arr.end());
        long long ans = 0;
        for(int i = 1; i < arr.size(); i++){
            arr[i] += arr[i - 1];
        }
        if(arr.back() <= target)return 0;

        for(int i = arr.size() - 1; i >=0 ;i--){
            if(arr[i] < target){
                long long p = pow(10, arr.size() - i - 1);
                long long tempNum = ((n/p) + 1)*p;
                return tempNum - n;
            }
        }
        return pow(10LL, arr.size()) - n;
    }
};
