class Solution {
public:
    int addMinimum(string s) {
        char should = 'a';
        int ans = 0;
        for(int i = 0; i < s.length();){
            if(should == 'd')should = 'a';
            if(s[i] == should){
                i++;
                should++;
            }else if(s[i] < should){
                ans += 'd' - should;
                should = 'a';
            }else {
                ans++;
                should++;
            }
        }
        ans += 'd' - should;
        return ans;
    }
};
