class Solution {
public:
    int fillCups(vector<int>& a) {
        priority_queue<int> q;
        for(auto i: a) {
            if(i != 0)
            q.push(i);
        }
        int ans = 0;
        while(q.size() > 1) {
            int t = q.top();
            q.pop();
            int r = q.top();
            q.pop();
            ans++;
            if(t - 1 > 0)
            q.push(t-1);
            if(r- 1 > 0)
            q.push(r-1);
        }
        if(q.size() == 1)  
        ans += q.top();

        return ans;


    }
};
