class Solution {
public:
    int minimumCardPickup(vector<int>& cards) {
        map<int, vector<int>> m;
        for(int i = 0 ; i < cards.size();i++){
            m[cards[i]].push_back(i);
        }
        int ans = 1e9;
        for(auto i : m){
            for(int j = 0; j < i.second.size() - 1;j++){
                ans = min(ans, i.second[j + 1] - i.second[j] + 1);
            }
        }
        if(ans == 1e9)return -1;
        return ans;
    }
};
