class Solution {
public:
    int minimumCost(vector<int>& cost) {
        sort(cost.begin(), cost.end(), greater<int>());
        int ans = 0;
        for(int i = 0; i < cost.size();i+=3){
            int c = cost[i];
            if(i != cost.size() - 1)c+= cost[i + 1];
            ans += c;
        }
        return ans;
    }
};
