class Solution {
public:
    long long minimumCost(string s) {
        int n = s.length();
        long long int ans = 1e18;
        long long pc = 0, sc = 0;
        for(int i = n - 2; i >= 0; i--){
            if(s[i] != s[i + 1]){
                sc += (n - i - 1);
            }
        }
        for(int i = 0; i < n - 1; i++){
            if(s[i] != s[i + 1]){
                pc += i + 1;
                sc -= (n - i - 1);
                ans = min(ans, (long long)(sc + pc));
            }
        }
        sc = 0, pc = 0;
        for(int i = 0; i < n - 1; i++){
            if(s[i] != s[i + 1]){
                pc += i + 1;
            }
        }
        for(int i = n - 2; i >= 0; i--){
            if(s[i] != s[i + 1]){
                sc += (n - i - 1);
                pc -= (i + 1);
                ans = min(ans, (sc + pc));
            }
        }
        if(ans == 1e18)return 0;
        return ans;
    }
};
