class Solution {
public:
    int ans=INT_MAX;
    int help1(string s){
        int a=s[0]-'0';

        return a;
    }
    int help2(string s){
        int a=s[1]-'0';

        int b=s[0]-'0';
        return b*10+a;
    }
    int help3(string s){
        int a=s[2]-'0';

        int b=s[1]-'0';

        int c=s[0]-'0';
        int ans=c*60;
        return ans+(b*10+a);
    }
    int help4(string s){
        if(s=="1000")
            cout<<s<<" ";
        int a=s[3]-'0';

        int b=s[2]-'0';

        int c=s[1]-'0';

        int d=s[0]-'0';
        int ans=d*10+c;
        ans*=60;
        ans+=(b*10+a);

        return ans;
    }
    void helper(int idx, int mc, int pc, int ts,int m,string time,int cost){
        if(time!=""){
            if(m==3&&help1(time)==ts){
                ans=min(ans,cost);
                return;
            }else if(m==2&&help2(time)==ts){
                ans=min(ans,cost);
                return;
            }else if(m==1&&help3(time)==ts){
                ans=min(ans,cost);
                return;
            }else if(m==0&&help4(time)==ts){
                ans=min(ans,cost);
                return;
            }

        }
        if(m==0) return;

        for(int i=0;i<=9;i++){
            int c=(idx==i)?0:mc;

                helper(i,mc,pc,ts,m-1,time+to_string(i),cost+c+pc);
        }


    }
    int minCostSetTime(int i, int mc, int pc, int ts) {
        helper(i,mc,pc,ts,4,"",0);
        helper(i,mc,pc,ts,3,to_string(i),pc);
        return ans;
    }
};
