class Solution {
public:
    int minDeletion(vector<int>& nums) {
        int ans=0;
        int elm=0;
        int cnt=0;
        for(int i=0;i<nums.size()-1;i++){
            if(cnt%2==0){
                if(nums[i]==nums[i+1]){
                    elm++;
                }else cnt++;
            }else cnt++;
        }
        if((nums.size()-elm)%2!=0){
            return elm+1;
        }
        return elm;
    }
};
