class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int i = 0, j = 0, ans = 1e9;
        sort(nums.begin(), nums.end());
        while(j < nums.size()){
            if(j - i + 1 <k)
                j++;
            else
                ans = min(ans, nums[j++] - nums[i++]);
        }
        return ans;
    }
};
