class Solution {
public:
    int minFlips(int a, int b, int c) {
        int ans = 0;
        for(int i = 0; i < 32; i ++){
            if((a & (1<<i)) == 0 && (b & ( 1<<i)) == 0 && (c&(1<<i)) > 0){
                ans++;
            }
            if((a & (1<<i)) > 0 && (c&(1<<i)) == 0){
                ans++;
            }
            if((b & (1<<i)) > 0 && (c&(1<<i)) == 0){
                ans++;
            }
        }
        return ans;
    }
};
