class Solution {
public:
    int minNumberOfHours(int initialEnergy, int initialExperience, vector<int>& energy, vector<int>& experience) {
        int ans = 0;
        int curr = initialExperience;
        int e = initialEnergy;
        for(int i = 0; i < energy.size(); i++){
            if(e > energy[i]){
                e -= energy[i];
            }else{
                ans += energy[i] - e + 1;
                e += energy[i] - e + 1;
                e -= energy[i];
            }
            if(curr > experience[i]){
                curr += experience[i];
            }else{
                ans += experience[i] - curr + 1;
                curr += experience[i] - curr + 1;
                curr += experience[i];
            }
        }
        return ans;
    }
};
