class Solution {
public:
    int minimumIndex(vector<int>& nums) {
        map<int, int> mp;
        for(auto i : nums)mp[i]++;
        int _max = 0, cnt = 0;
        for(auto i : mp){
            if(i.second > cnt)_max = i.first, cnt = i.second;
        }
        int a = mp[_max];
        int b = 0;
        for(int i = 0; i < nums.size() - 1; i++){
            if(nums[i] == _max){
                b++;
                a--;
            }
            if(b * 2 > (i + 1) && a * 2 > (nums.size() - (i + 1)))return i;
        }
        return -1;
    }
};
