class Solution {
public:
    int minimumSize(vector<int>& nums, int maxOperations) {
        int lo = 1, hi = *max_element(nums.begin(), nums.end()), ans = 0;
        while(lo <= hi){
            int mid = (hi + lo)>>1, op = 0;
            for(auto i : nums)
                op += (i - 1)/mid;
            if(op <= maxOperations){
                ans = mid;
                hi = mid - 1;
            }else{
                lo = mid + 1;
            }
        }
        return ans;
    }
};
