class Solution {
public:
    int minMoves(vector<int>& nums) {
       int sum = 0;
        for(auto i : nums) {
            sum += i;
        }

        int min_element = INT_MAX;
        for(auto i : nums) {
            min_element = min(min_element, i);
        }

        return sum - min_element*nums.size();
    }
};
