class Solution {
public:
    int minMoves(int t, int d) {
        if(!d) return t-1;
        else{
                int count=0;
                while(d--){
                    if(t==1) break;
                    if(t%2!=0){
                        count++;
                    }
                    t/=2;
                    count++;

                }
                if(t==1) return count;
                return count+t-1;
            }


        return 0;
    }

};
