class Solution {
public:
    int minDays(vector<int>& bloomDay, int m, int k) {
        if(bloomDay.size()/k < m)return -1;
        long lo = 0, hi = 1e9, ans = 0;
        while(lo <= hi){
            long mid = (lo + hi)/2;
            long cnt = 0, t = 0;
            for(auto i : bloomDay){
                if(i <= mid){
                    cnt++;
                }else{
                    cnt = 0;
                }
                if(cnt >= k){
                    t++;
                    cnt = 0;
                }
            }
            if(cnt >= k)t++;
            if(t >= m){
                ans = mid;
                hi = mid - 1;
            }else{
                lo = mid + 1;
            }
        }
        return ans;
    }
};
