class Solution {
public:
    int minKBitFlips(vector<int>& nums, int k) {
        int n = nums.size();
        int cnt = 0;
        queue<int> q;

        for(int i = 0; i < n; i++) {
            if(nums[i] == 0) {
                if(q.empty() or q.size() % 2 == 0) {
                    cnt++;
                    q.push(i + k - 1);
                }
            }
            else {
                if(q.size() % 2 != 0) 
                {
                    cnt++;
                    q.push(i + k - 1);
                }


            }

            if(!q.empty() and i >= q.front()) {
                q.pop();
            }


        }
        return q.size() == 0 ? cnt : -1;
    }
};
