class Solution {
public:
    int minOperations(vector<int>& nums) {
        bool f = false;
        int one = 0, g = 0;
        for (auto i : nums)
        {
            g = __gcd(i, g);
            if (i == 1)
                one++;
        }
        for (int i = 0; i < nums.size() - 1; i++)
        {
            if (__gcd(nums[i], nums[i + 1]) == 1)
                f = true;
        }
        if (f)
            return nums.size() - one;
        if (g > 1)
            return -1;
        int _min = nums.size();
        for (int i = 0; i < nums.size(); i++)
        {
            int gcd = 0;
            for (int j = i; j < nums.size(); j++)
            {
                gcd = __gcd(gcd, nums[j]);
                if (gcd == 1)
                {
                    _min = min(_min, j - i + 1);
                }
            }
        }
        return nums.size() + (_min - 2);
    }
};
