class Solution {
public:
    int helper(int n){
        if(n == 2)return 1;
        if(n%3 == 0)return n/3;
        if(n%3 == 2)return n/3 + 1;
        return n/3 - 1 + 2;
    }
    int minOperations(vector<int>& nums) {
        map<int, int> mp;
        for(auto i : nums)mp[i]++;
        for(auto i : mp)if(i.second==1)return -1;
        int ans = 0;
        for(auto i : mp)ans += helper(i.second);
        return ans;
    }
};
