class Solution {
public:


    int minRefuelStops(int t, int s, vector<vector<int>>& v) {
       if(t <= s) {
           return 0;
       }

        v.push_back({t, 0});
        int ans = 0;
        int prev = 0; 
        priority_queue<int> q;
        for(auto i : v) {
            int pos = i[0];
            int cap = i[1];

            s -= (pos-prev);
            while(!q.empty() and s < 0) {
                s += q.top();
                q.pop();
                ans++;
            }

            if(s < 0) return -1;

            q.push(cap);
            prev = pos;

        }

       if(s < 0) return -1;
        return ans;



    }
};
