class Solution {
public:
    int minSteps(string s, string t) {
        map<char, int> m1;
        map<char, int> m2;
        for(auto i : s){
            m1[i-'a']++;
        }
        for(auto i : t) {
            m2[i-'a']++;
            }

        int cnt = 0;

        for(auto i : t) {
            if(m1[i-'a'] < m2[i-'a']) {
                cnt+=(m2[i-'a'] - m1[i-'a']);
                m1[i-'a'] = m2[i-'a'];
            }
        }

        cout << cnt<<endl;
        for(auto i : s) 
        {
            if(m2[i-'a'] < m1[i-'a']) {
                cnt+=(m1[i-'a'] - m2[i-'a']);
                m2[i-'a'] = m1[i-'a'];
            }
        }
        return cnt;
    }
};
