class Solution {
public:
    int minTaps(int n, vector<int>& ranges) {        
        int dp[n + 100];
        for(int i = 0; i <= n; i++) {
            dp[i] = n + 2;
        }
        dp[0] = 0;
        // for(int i = 0; i <= ranges[0]; i++) {
        //     dp[i] = 0;
        // }

        for(int i = 0; i <= n; i++) {
            for(int j = max(0, i - ranges[i]); j <= (n, i + ranges[i]); j++) {
                dp[j] = min(dp[j], 1 + dp[max(0, i - ranges[i])]);
            }
        }

        // for(int i = 0; i <= n; i++){
        //     cout << dp[i] << ' ';
        // }
        // cout << endl;
        if(dp[n] >= n + 2) {
            return -1;
        }
        return dp[n];

    }
};
