class Solution {
public:
    int minOperations(vector<int>& nums, int k) {
        int ans = 0;
        int _max = -1e9;
        int _min = 1e9;
        map<int, int> mp;
        for(int i = nums.size() - 1; i >=0; i--){
            mp[nums[i]]++;
            ans++;
            int f = 0;
            for(int j = 1; j <= k; j++){
                if(mp[j] == 0){
                    f = 1;
                }
            }
            if(f)continue;
            return ans;
        }
        return -1;

    }
};
